    var smallButtonSize = 156 / timestamps.length;
    for (i = 0; i < timestamps.length; i += 1) {
        createSliderButton("btnSlider_" + i, "", 0, overall_left+(smallButtonSize*i), overall_bottom+25, smallButtonSize, 20, true, "goToTimestepIndex(" + i + ");")
    }

