    var current_index = 0;

    function goToFirstTimestep() {
        for (var i = 0; i < timestamps.length; i += 1) {
                for (var j = 0; j < timestamps[i].ocgs.length; j += 1) {
                    ocg_name = timestamps[i].ocgs[j];
                    ocg = getOCGByName(ocg_name);
                    ocg.state = false;
                }
            }
        current_index = -1;
        nextTimestep();
    }

    function nextTimestep() {
        current_index += 1;

        if (current_index > timestamps.length - 1) {
            current_index = timestamps.length - 1;
            return;
        }

        var ocg = getOCGByName(timestamps[current_index].ocgs[0]);
        ocg.state = true;
    }

    function prevTimestep() {
        var ocg = getOCGByName(timestamps[current_index].ocgs[0]);
        ocg.state = false;

        current_index -= 1;

        if (current_index < 0) {
            current_index = 0;
            return;
        }
    }

    function runAnimation() {
        progress = app.thermometer;
        animate = app.setInterval("animateTimestamps()", intervalVal);
        animate.position = startVal;

    } // function runAnimation()

    function getOCGByName(name) {
        var ocgs = this.getOCGs();

        for (var i = 0; i < ocgs.length; i += 1) {
            if (ocgs[i].name == name) {
                return ocgs[i];
            }
        }

        return false;
    }

    function animateTimestamps() {
        // called repeatedly by the app to run the animation

        var ocgs = this.getOCGs();
        var index = Math.abs(animate.position - startVal);

        if (progress.cancelled) {
            // user cancelled
            app.clearInterval(animate);
            progress.end();
            progerss = null;
        } else if (animate.position < startVal && animate.position < endVal) {
            // end of animation
            app.clearInterval(animate);
            progress.end();
            progerss = null;
        } else if (animate.position > startVal && animate.position > endVal) {
            // end of animation
            app.clearInterval(animate);
            progress.end();
            progerss = null;
        } else if (animate.position == startVal) {
            // start of animation

            for (var i = 0; i < timestamps.length; i += 1) {
                for (var j = 0; j < timestamps[i].ocgs.length; j += 1) {
                    ocg_name = timestamps[i].ocgs[j];
                    ocg = getOCGByName(ocg_name);
                    ocg.state = false;
                }
            }

            progress.duration = Math.abs(startVal - endVal);
            progress.begin();

            for (var i = 0; i < timestamps[animate.position].ocgs.length; i += 1) {
                ocg_name = timestamps[animate.position].ocgs[i];
                ocg = getOCGByName(ocg_name);
                ocg.state = true;
            }

            progress.value = index;
            progress.text = timestamps[animate.position].name;

            animate.position += stepVal;
        } else {
            for (var i = 0; i < timestamps[animate.position].ocgs.length; i += 1) {
                ocg_name = timestamps[animate.position].ocgs[i];
                ocg = getOCGByName(ocg_name);
                ocg.state = true;
            }

            progress.value = index;
            progress.text = timestamps[animate.position].name;

            animate.position += stepVal;
        }

    } // function animateTimestamps()

    function createButton(cName, caption, nPage, xOffset, yOffset, jsCode) {
	// Acquire the crop box (visible area) for the current page
	var pgRect = this.getPageBox("Crop", nPage);
	// Build Button Rectangle in lower left corner of page
	var fldRect = [];
    fldRect[0] = pgRect[0] + xOffset;
	// 1/2 inch from left side
	fldRect[1] = pgRect[0] + yOffset + 36;
	// 20 points high
	fldRect[2] = pgRect[0] + xOffset + 36;
	// 1/2 inch wide
	fldRect[3] = pgRect[0] + yOffset;
	// 1/2 inch from bottom
	// Create Button on page
	var oFld = this.addField( cName , "button", nPage, fldRect);
	// Setup Button's Properties
	if (oFld != null) {
		oFld.buttonSetCaption(caption);
		oFld.strokeColor = color.black;
		oFld.textColor = color.black;
        oFld.lineWidth = 2;
		oFld.setAction("MouseUp", jsCode);
	}
	return oFld;
}
    createButton("btnPrev", "Prev", 0, 100, 100, "prevTimestep();")
    createButton("btnReset", "Reset", 0, 150, 100, "goToFirstTimestep();")
    createButton("btnNext", "Next", 0, 200, 100, "nextTimestep();")

