function getAnimationConfig(doc) {
    // collect animation config info

    var configDialog = {
        initialize: function (dialog) {
            var startVals = {};
            var endVals = {};
            for (var i = 0; i< timestamps.length; i += 1) {
                var prefix;
                if (timestamps.length < 10)
                    prefix = util.printf("%d: ", i);
                else if (timestamps.length < 100)
                    prefix = util.printf("%02d: ", i);
                else
                    prefix = util.printf("%03d: ", i);

                // selected values are positive, all others must be positive
                if (i == 0) {
                    // default value
                    startVals[prefix + timestamps[i].name] = i + 1;
                } else {
                    startVals[prefix + timestamps[i].name] = -(i + 1);
                }
                if (i == timestamps.length - 1) {
                    // default value
                    endVals[prefix + timestamps[i].name] = i + 1;
                } else {
                    endVals[prefix + timestamps[i].name] = -(i + 1);
                }
            }

            dialog.load({
                fld1: startVals,
                fld2: endVals,
                fld3: "1000"
            });
        },
        commit: function (dialog) // called when OK is clicked
        {
            var results = dialog.store();
            for (prop in results["fld1"]) {
                var val = results["fld1"][prop];
                if (val > 0)
                    startVal = val - 1;
            }
            for (prop in results["fld2"]) {
                var val = results["fld2"][prop];
                if (val > 0)
                    endVal = val - 1;
            }
            intervalVal = results["fld3"];
        },
        description: {
            name: "Configure Timestamp Animation",
            align_children: "align_left",
            width: 350,
            height: 200,
            elements: [{
                    type: "cluster",
                    name: "",
                    align_children: "align_left",
                    elements: [{
                            type: "view",
                            align_children: "align_row",
                            elements: [{
                                    type: "static_text",
                                    name: "Start: "
                                },
                                {
                                    item_id: "fld1", // must be 4 characters
                                    type: "popup",
                                    alignment: "align_fill",
                                    width: 300,
                                    height: 24
                                }
                            ]
                        },
                        {
                            type: "view",
                            align_children: "align_row",
                            elements: [{
                                    type: "static_text",
                                    name: "End: "
                                },
                                {
                                    item_id: "fld2", // must be 4 characters
                                    type: "popup",
                                    alignment: "align_fill",
                                    width: 300,
                                    height: 24
                                }
                            ]
                        },
                        {
                            type: "view",
                            align_children: "align_row",
                            elements: [{
                                    type: "static_text",
                                    name: "Interval: "
                                },
                                {
                                    item_id: "fld3", // must be 4 characters
                                    type: "edit_text",
                                    alignment: "align_fill",
                                    width: 40,
                                    height: 24
                                },
                                {
                                    type: "static_text",
                                    name: "milliseconds"
                                }
                            ]
                        }
                    ]
                },
                {
                    alignment: "align_right",
                    type: "ok_cancel",
                    ok_name: "Ok",
                    cancel_name: "Cancel"
                }
            ]
        }
    }; // var configDialog

    var result = app.execDialog(configDialog);
    return result;

} // function getAnimationConfig()


var result = getAnimationConfig(this);

if (result == "ok") {
    var stepVal = 0;
    if (startVal < endVal)
        stepVal = 1;
    else
        stepVal = -1;


    runAnimation();
}
